  return new Promise(function(resolve, reject) {
    var transporter = nodemailer.createTransport({
      service: 'gmail',
      port: 587,
      secure: false,
      auth: {
        user: email.email,
        pass: email.password
      }
    });
  
  var mailOptions = {
    from: 'faanmanagement@gmail.com  <http://boomadvertise.com/>',
    to: to,
    subject: subject,
    html: html
  };

    transporter.sendMail(mailOptions, function(error, info){
    if (error) {
      reject(error);
    } else {
     resolve(1)
    }
  });

  
});

}

